import React, { useState, useEffect } from 'react';
import { useNavigate } from "react-router-dom";
import Left from './Left';
function Cart() {
  const navigate = useNavigate();
  const [cart, setCart] = useState([]);

  const fetchCart = async () => {
    try {
      const res = await fetch('/cart', {
        method: "GET",
        headers: {
          Accept: "application/json",
          "Content-Type": "application/json"
        },
        credentials: "include"
      });
      const data = await res.json();
      console.log(data);
      setCart(data);
      if (!res.ok) {
        throw new Error(res.error);
      }
    } catch (err) {
      console.log(err);
      navigate('/Login');
    }
  };

  useEffect(() => {
    fetchCart();
  }, []);

  return (
    <div>
      <h2>Cart Table</h2>
      <table className="table table-dark">
        <thead>
          <tr>
            <th>S.No</th>
            <th>ID</th>
            <th>Item Name</th>
            <th>Quantity</th>
            <th>Price</th>
          </tr>
        </thead>
        <tbody>
          {cart.map((item, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{item.id}</td>
              <td>{item.name}</td>
              <td>{item.quantity}</td>
              <td>{item.price}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Cart;
