import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

function Items() {
  const navigate = useNavigate();
  const [items, setItems] = useState([]);

  const fetchItems = async () => {
    try {
      const res = await fetch('/api/items', {
        method: 'GET',
        headers: {
          Accept: 'application/json',
          'Content-Type': 'application/json'
        },
        credentials: 'include'
      });
      const data = await res.json();
      console.log(data);
      setItems(data);
      if (!res.ok) {
        throw new Error(res.error);
      }
    } catch (err) {
      console.log(err);
      navigate('/Login');
    }
  };

  useEffect(() => {
    fetchItems();
  }, []);

  return (
    <div>
      <h2>Item Table</h2>
      <table className="table table-dark">
        <thead>
          <tr>
            <th>S.No</th>
            <th>ID</th>
            <th>Name</th>
            <th>Category</th>
            <th>Price</th>
            <th>Brand</th>
          </tr>
        </thead>
        <tbody>
          {items.map((item, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              <td>{item.id}</td>
              <td>{item.name}</td>
              <td>{item.category}</td>
              <td>{item.price}</td>
              <td>{item.brand}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Items;
