import React,{useState, useEffect} from 'react';
function Contact() {
    
    const [userData,setUserData]=useState({name:"",email:"",phone:"",message:""});
    const userContact=async()=>{
        try{
            const res=await fetch('/getdata',{
                method:"GET",
                headres:{
                    "Content-Type":"application/json"
                },
            });
            const data=await res.json();
            console.log(data);
            serUserData({...setUserData,name:data.name,email:data.email,phone:data.phone});
            if(!res.status===200)
            {
                const error=new Error(res.error);
                throw error;
            }
        }catch(err){
            console.log(err);
        }
    }
    useEffect(()=>
    {
        userContact();
    },
    []);
    const handleInput=(e)=>{
        e.preventDefault();
        const name=e.target.name;
        const value=e.target.value;
        setUserData({...setUserData,[name]:value});

    }
    const contactForm=async(e)=>{
        e.preventDefault();
        const {name,email,phone,message}=userData;
        const res= await fetch('/contact',{
            method:"POST",
            headers:{
                "Content-Type":"application/json"
            },
            body:JSON.stringify({
                name,email,phone,message
            })
        });
        const data =await res.json();
        if(!data){
            console.log("message send");
        }
        else{
            alert("message send");
            setUserData({...userData,message:""});
        }
    }
    return (
        <>

            <div class="container mt-5">
                <h2>Contact Form</h2>
                <form method="post" action="/Contactusform">
                    <div class="form-group">
                        <label for="name">Name:</label>
                        <input type="text" class="form-control" id="name" value={userData.name} onChange={handleInput} placeholder="Enter your name" name="name" required/>
                    </div>
                    <div class="form-group">
                        <label for="email">Email:</label>
                        <input type="email" class="form-control" id="email" value={userData.email} onChange={handleInput}placeholder="Enter your email" name="email" required />
                    </div>
                    <div class="form-group">
                        <label for="phone">Phone:</label>
                        <input type="text" class="form-control" id="phone" value={userData.phone} onChange={handleInput}placeholder="Enter your phone number" name="phone" required/>
                    </div>
                    <div class="form-group">
                        <label for="comment">Comment:</label>
                        <input type="text" class="form-control" id="message" value={userData.message} onChange={handleInput}placeholder="Enter your phone number" name="message" required/>
                    </div>
                    <button type="submit" class="btn btn-primary" onClick={contactForm}>Submit</button>
                </form>
            </div>
        </>
    )
}

export default Contact;
