import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Cart() {
  const [cartItems, setCartItems] = useState([]);
  const [formData, setFormData] = useState({
    id: '',
    itemName: '',
    quantity: 0,
    price: '',
  });

  useEffect(() => {
    fetchCartItems();
  }, []);

  const fetchCartItems = async () => {
    try {
      const response = await axios.get('/api/cart');
      setCartItems(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.id]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/cart', formData);
      // Item added successfully, do something
      console.log('Item added to cart');
      fetchCartItems(); // Refresh the cart items after adding a new item
    } catch (error) {
      // Error occurred while adding item to cart, handle error
      console.error(error);
    }
  };

  return (
    <div>
      <h2>Cart</h2>
      <form onSubmit={handleSubmit}>
        {/* Form fields */}
        {/* ... */}
        <button type="submit" className="btn btn-primary">
          Add to Cart
        </button>
      </form>

      {/* Display cart items */}
      <h3>Cart Items</h3>
      <ul>
        {cartItems.map((item) => (
          <li key={item.id}>
            {item.itemName} - Quantity: {item.quantity} - Price: {item.price}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Cart;
